# Define an error for wrong pdb fileclass WrongPDBfile(Exception):    """Informs about a wrong file."""        def __init__(self, file):        self.file = file        def __str__(self):        return("The file %s is not valid, a pdb file is required." % self.file)def pdb_get_prot(infile):    """Aislates the protein from a pdb file."""        if infile.find(".pdb") == -1:        raise WrongPDBfile(infile)        # In case of a relative path    if infile.find("/") != -1:        pdbname = infile[-infile[::-1].find("/"):][:-4]    else:        pdbname = infile[:infile.find(".")]        outfile = "results_" + pdbname + "/" + pdbname + "_protein.pdb"    out = open(outfile, "w")    for line in open(infile):        line = line.rstrip()        linelist = line.split()                if linelist[0] == "ATOM":            out.write(line + "\n")            out.close()    return outfile